import React, { Component } from "react";
import { connect } from "react-redux";
import { Image, ImageBackground, Text, View } from "react-native";

import Colors from "../../constants/Colors";
import Layout from "../../constants/Layout";
import Label from "../../components/elements/Label";
import Hint from "../../components/elements/Hint";
import H3 from "../../components/elements/H3";
import H2 from "../../components/elements/H2";
import { Feather, MaterialCommunityIcons, FontAwesome } from "@expo/vector-icons";
import moment from "moment";

// const HALAL_BG = require("../../assets/images/background/banner3.jpg");

class CatalogItem extends Component {
  static propTypes = {};
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    const { data } = this.props;

    return (
      <View
        style={[
          {
            // backgroundColor: "rgb(27,27,29)",
          },
        ]}
      >

        <View
          style={[
            {
              position: "relative",
              flexDirection: "row",
              alignItems: "flex-end",
              justifyContent: 'space-between',
              borderWidth: this.props.noLine ? 0 : 0.2,
              // padding: Layout.screenPadding,
              height: 250,
              margin: 30,
              borderColor: "rgb(27,27,29)",
              borderRadius: 20,
              borderBottomColor: 'rgb(27,27,29)',
              // backgroundColor: "rgb(27,27,29)",
            },
          ]}
        >
          <ImageBackground 
            // source={HALAL_BG} 
            resizeMode="cover" 
            style={{
              position: "absolute",
              width: '100%',
              height: '100%',
              overflow: "hidden",
              borderRadius: 20,
              borderWidth: 0.2,
              borderColor: 'rgb(27,27,29)',
            }}>
          </ImageBackground>
            <View
              style={{
                width: `100%`,
                // width: Layout.window.width - 15 - Layout.screenPadding * 2,
              }}
            >

              <View
                style={{
                  position: "absolute",
                  bottom: 0,
                  left: 0,
                  width: '100%',
                  display: 'flex',
                  flexDirection: "row",
                  alignItems: "center",
                  justifyContent: 'space-between',
                  // gap: 10,
                  padding: 15,
                  borderWidth: 0.2,
                  borderColor: "black",
                  backgroundColor: "rgb(27,27,29)",
                  borderBottomLeftRadius: 20,
                  borderBottomRightRadius: 20,
                  height: 60,
                  shadowColor: "black",
                  shadowOffset: {
                      width: 0,
                      height: 2
                  },
                  shadowOpacity: 1,
                  shadowRadius: 4,
                }}
              >
                <View 
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    paddingLeft: 20,
                  }}
                >
                  <Feather name="clock" size={Layout.platform === 'ios' ? 31 : 29} color={"rgb(242,210,152)"} />
                  <View style={{ paddingLeft: 5 }}>
                    <Text style={{ color: "rgb(242,210,152)", fontSize: 12, fontWeight: '600' }}>Validity</Text>
                    <Text style={{ color: "rgb(242,210,152)", fontWeight: '300'}}>{moment(data.psvchexp).format("MM/DD/YYYY")}</Text>
                  </View>
                </View>

                <View 
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    paddingRight: 20,
                  }}
                >
                  {/* <MaterialCommunityIcons name="currency-usd" style={{ fontSize: 29, color: "rgb(242,210,152)" }} /> */}
                  <View>
                    <Feather name="circle" size={Layout.platform === 'ios' ? 31 : 29} color={"rgb(242,210,152)"} />
                    <Feather name="dollar-sign" size={Layout.platform === 'ios' ? 16 : 14} color={"rgb(242,210,152)"}
                      style={{
                        position: "absolute",
                        paddingLeft: 8,
                        paddingTop: 7,
                      }}
                    />
                  </View>

                  <View style={{ paddingLeft: 5 }}>
                    <Text  style={{ color: "rgb(242,210,152)", fontSize: 12, fontWeight: '600' }}>Minimum Spend</Text>
                    <Text style={{ color: "rgb(242,210,152)", fontWeight: '300'}}>RM {data.psvchmin}</Text>
                  </View>
                </View>
              </View>
            </View>
        </View>
      </View>
    );
  }
}

const mapStateToProps = (state) => ({
  // apiCallCount: state.home.apiCallCount,
});

export default connect(mapStateToProps)(CatalogItem);
